import { useContext } from 'react';
import { CartContext } from '../../contexts/cart.context';
import { useNavigate } from "react-router-dom";

import Button from '../button/button.component';
import CartItem from '../cart-icon/cart-item/cart-item.component';

import {CartDropdownContainer, EmptyMesage} from './card-dropdown.styles';


const CardDropdown = () => {

    const {cartItems} = useContext(CartContext);
    const navigate = useNavigate();

    const gotToCheckoutHandler = () => {
        navigate('/checkout');
    }

    return (
        <div className='cart-dropdown-container'>
            <div className="cart-items">
                {
                    cartItems.length ? (cartItems.map(item => <CartItem key={item.id} cartItem={item} />)) : (
                        <span>Your card is empty</span>
                    )
                }


            </div>
            <Button onClick={gotToCheckoutHandler}>Go to checkout</Button>
        </div>
    );
}


export default CardDropdown;
